effectors.argb:
//red(0,1,1)
//green(0,1,1)
//blue(0,1,1)
//alpha(0,1,1)

void argb(float alpha, float red, float green, float blue) { 

	color = color * vec4 (red, green, blue, alpha);  

}

effectors.atlas:
//id(0,10,0)

void atlas(int id) { 

	uv *= media[id].size;
	uv += media[id].pos;
	color *= texture(medias, uv);

}
