effectors.argb:
//red(0,1,1)
//green(0,1,1)
//blue(0,1,1)
//alpha(0,1,1)

void argb(float alpha, float red, float green, float  blue) { 

	color = color * vec4 (red, green, blue, 0) * alpha; 

}

effectors.rectangle:
//size(0,1,1) 
//pos(0,1,0.5)
//orientation(0,1,0.5) 

void rectangle(vec2 pos, vec2 size, float orientation) {


	uv = uv - pos - pos*size + size*0.5;

	orientation = (orientation-.5)*6.2831853072;

	float c = cos(orientation);
	float s = sin(orientation);

	uv *= aspect_ratio;
	uv = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);
	uv *= 1./aspect_ratio;

	vec2 tuv = abs(uv) - size * 0.5;
	float dist = length(max(tuv, 0.0)) + min(max(tuv.x, tuv.y), 0.0);

	uv/=size;

	uv+=.5;

	color *= vec4(vec3(1.0 - step(0.0, dist)), 1.0);

}

effectors.flower:
//inratio(0,1,0)
//shape(0,1,0)
//petals(0,1,0)

void flower(float inratio, float shape, float petals) {

	uv-=.5;

	float c = length(uv)+.5;

	float a = atan(uv.x,uv.y);//+iTime;

	float r = a * ((petals*29.)+3.);//  - rotation*3.14*2.;

	r = abs(fract(.5/3.14*r)-.5)*2.;

	r = pow(r,.5+pow(shape*2.,4.)); // petal type

	r = 1.-r*inratio*.5;//; // spikes out

	r = step(c,r);

	color *= vec4(r);

}

effectors.gradient:
void gradient(float exp, float angle) {
	angle *= 6.28318530718;
	float s = sin(angle);
	float c = cos(angle);
	uv *= mat2(c, -s, s, c);
	uv += .5;
	uv += 1-exp*2;
	color *= vec4(uv.x);}

effectors.burst:
void burst(float inratio, float shape, float petals) {

	uv -= .5;

	shape = 1-shape;

	float c = length(uv)+.5;

	float a = atan(uv.x,uv.y);//+iTime;

	float r = a * ((petals*29.)+3.);//	- rotation*3.14*2.;

	r = abs(fract(.5/3.14*r)-.5)*2.; // equals : abs(mod(.5/3.14*r,1.)-.5); equals sdTriangle(a,petals);

	r = clamp(r*c*.9,0.,1.);

	r += (1.-inratio*2.); // gradient to white

	float zzz = shape*.4999;
	r = smoothstep(zzz,1.-zzz, r);

	r = mix(0.,1.,r);

	color *= r;

uv += .5;

}
