effectors.argb.glsl:
//red(0,1,1)
//green(0,1,1)
//blue(0,1,1)
//alpha(0,1,1)

void argb(float alpha, float red, float green, float  blue) { 

	color = color * vec4 (red, green, blue, 0) * alpha; 

}

effectors.atlas.glsl:
//id(0,10,0)

void atlas(float id_) {

	int id = int(id_*10);

	vec2 tuv = uv;

	tuv *= static_ubo.media[id].size;
	tuv += static_ubo.media[id].pos;
	color *= texture(medias, tuv);

}

effectors.feedback.glsl:
//intensity(0,1,0)

void feedback(float intensity) { 

	color += ( texture( render_pass, NORMALIZED) - .002 ) * pow(abs(intensity),.1);

}

effectors.uber.glsl:
//id(0,10,0)
//q(0,10,1)

void uber(int id, int q) {

	vec4 color_=  vec4(0);

	for (int i =0; i < q; i++) {

		vec2 tuv = uv;

		tuv *= static_ubo.ubervbo[id+i].uberLayer.size;
		tuv += static_ubo.ubervbo[id+i].uberLayer.norm;

		color_ += texture(uberlayer, tuv);

	}

	color *= color_;

};

effectors.rectangle.glsl:
//size(0,1,1) 
//pos(0,1,0.5) 

void rectangle(vec2 pos, vec2 size, float angle) {



	pos *= 1+size;
	pos -= size*.5;
	uv -= pos; 

	angle *= 6.2831853072;

	float s = sin(angle);
	float c = cos(angle);

	uv *= aspect_ratio * mat2(c, -s, s, c) * (1./aspect_ratio);

	uv /= size; 
	uv += .5;

	if (uv.x > 1. || uv.y > 1. || uv.x < 0. || uv.y < 0. ) color = vec4(0);

}
