effectors.argb.glsl:
//red(0,1,1)
//green(0,1,1)
//blue(0,1,1)
//alpha(0,1,1)

void argb(float alpha, float red, float green, float  blue) { 

	color = color * vec4 (red, green, blue, 0) * alpha; 

}

effectors.atlas.glsl:
//id(0,10,0)

void atlas(int id) {

	vec2 tuv = uv;

	tuv *= media[id].size;
	tuv += media[id].pos;
	color *= texture(medias, tuv);

}

effectors.feedback.glsl:
//intensity(0,1,0)

void feedback(float intensity) { 

color += ( texture( render_pass, UV) - .002 ) * pow(abs(intensity),.1);

}

effectors.rectangle.glsl:
//size(0,1,1) 
//pos(0,1,0.5) 

void rectangle(vec2 pos, vec2 size) {

	float angle = 0;

	pos *= 1+size;
	pos -= size*.5;
	uv -= pos; 

	uv /= size; 
	uv += .5;

	if (uv.x > 1. || uv.y > 1. || uv.x < 0. || uv.y < 0. ) color = vec4(0);

}

vec2 rotate(vec2 v, float a) {

	float s = sin(a);
	float c = cos(a);

	return v * mat2(c, -s, s, c);
}
