effectors.argb.glsl:
//red(0,1,1)
//green(0,1,1)
//blue(0,1,1)
//alpha(0,1,1)

void argb(float alpha, float red, float green, float  blue) { 

	color = color * vec4 (red, green, blue, 1) * alpha; 

}

effectors.rectangle.glsl:
//size(0,1,.5) 
//pos(0,1,0.5)
//orientation(0,1,0.5) 

void rectangle(vec2 pos, vec2 size, float orientation) {


	uv = uv - pos - pos*size + size*0.5;

	orientation = (orientation-.5)*6.2831853072;

	float c = cos(orientation);
	float s = sin(orientation);

	uv *= aspect_ratio;
	uv = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);
	uv *= 1./aspect_ratio;

	vec2 tuv = abs(uv) - size * 0.5;
	float dist = length(max(tuv, 0)) + min(max(tuv.x, tuv.y), 0);

	uv/=size;

	uv+=.5;

	color *= vec4(vec3(1 - step(0, dist)), 1);

}
